import React, { useState } from 'react';

const Test1 = () => {
    const [data,setData]=useState([
        {id:1, text:'영화보기'},
        {id:2, text:'바다가기'},
        {id:3, text:'한강가기'},
    ])
    
    return (
        <div>
            <h1>할일 만들기</h1>
            <Form/>
            <List data={data}/>
        </div>
    );
};

const Form = () => {
    return (
        <div>
            <input type="text"/>
            <button>추가</button>
        </div>
    );
};

const List = ({data}) => {
    return (
        <div>
            {
                data.map(item => <Item key={item.id} item={item}/>)
            }
        </div>
    );
};

const Item = ({item}) => {
    const {id,text} = item
    return (
        <div>
            {id} / {text}
            <button>삭제</button>
        </div>
    );
};


export default Test1;
