import React, { useState, useRef } from 'react';
import TodoList from './TodoList';
import TodoInput from './TodoInput';
import './Todos.css'

const Todos = () => {
    const no = useRef()
    const[todos, setTodos] = useState([
        {id:1, text:'친구만나기',done:false}
    ])

    const onAdd = (text) => {
        setTodos([
            ...todos,
            {
                id:no.current++,
                text,
                done:false
            }
        ])

    }
    // 삭제
    const onDel = (id) => {
