import React, { useState, useRef } from 'react';
import TodoList from './TodoList';
import TodoInput from './TodoInput';
import './Todos.css'

const Todos = () => {
    const no = useRef()
    const[todos, setTodos] = useState([
        {id:1, text:'친구만나기',done:false}
    ])
    return (
        <div className="Todos">
            <h1>Todo List</h1>
            <TodoInput/>
            <TodoList todos={todos}/>
        </div>
    );
};

export default Todos;
